import React, { useEffect, useState } from "react";
import {
  View,
  Text,
  FlatList,
  ScrollView,
  TouchableOpacity,
  TextInput,
  StyleSheet,
} from "react-native";
import { useNavigation } from "@react-navigation/native";
import axios from "axios";
import { API_BASE_URL, API_ROUTES } from "../../constants/API";

import { tableStyles } from "../../constants/Colors";

import { useUser } from "../../contexts/UserContext";

const MaterialListScreen = ({ navigation }) => {
  const [material, setMaterial] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");

  useEffect(() => {
    const fetchMaterial = async () => {
      try {
        const response = await axios.get(
          `${API_BASE_URL}${API_ROUTES.MATERIALES}`
        );
        setMaterial(response.data);
        console.log(response);
      } catch (error) {
        console.error("Error al obtener material", error);
      }
    };

    fetchMaterial();
  }, []);

  const handleSearch = (text) => {
    setSearchQuery(text);
    const filteredMaterial = material.filter((item) =>
      item.titulo.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setMaterial(filteredMaterial);
  };

  const renderTableHeader = () => (
    <View style={tableStyles.tableHeader}>
      <Text style={tableStyles.headerText}>Titulo</Text>
      <Text style={tableStyles.headerText}>Autor</Text>
    </View>
  );

  const renderItem = ({ item }) => (
    <TouchableOpacity onPress={() => handleMaterialPress(item.id)}>
      <View style={tableStyles.tableRow}>
        <Text style={tableStyles.cell}>{item.titulo}</Text>
        <Text style={tableStyles.cell}>{item.autor}</Text>
        <Text style={tableStyles.cell}>{item.genero}</Text>
      </View>
    </TouchableOpacity>
  );

  const handleMaterialPress = (materialId) => {
    navigation.navigate("DetalleMaterial", { materialId });
  };
  return (
    <View style={styles.container}>
      <View style={{ padding: 10 }}>
        <TextInput
          style={styles.searchInput}
          placeholder="Buscar material..."
          value={searchQuery}
          onChangeText={handleSearch}
        />
      </View>
      <ScrollView>
        {renderTableHeader()}
        <FlatList
          data={material}
          keyExtractor={(item) => item.id.toString()}
          renderItem={renderItem}
        />
      </ScrollView>
    </View>
  );
};
const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  searchBar: {
    padding: 10,
    backgroundColor: "#fff",
    borderBottomWidth: 1,
    borderBottomColor: "#ddd",
  },
  searchInput: {
    height: 40,
    borderColor: "gray",
    borderWidth: 1,
    padding: 10,
  },
});
export default MaterialListScreen;
