import React, { useEffect, useState } from "react";
import { Text, View, StyleSheet, ScrollView, FlatList } from "react-native";
import {
  Button,
  Dialog,
  PaperProvider,
  Paragraph,
  Portal,
  TextInput,
  Appbar,
} from "react-native-paper";
import { tableStyles } from "../../constants/Colors";
import { useNavigation, useRoute } from "@react-navigation/native";
import axios from "axios";
import { API_BASE_URL, API_ROUTES } from "../../constants/API";
import { toast } from "react-toastify";

import { useUser } from "../../contexts/UserContext";

import AsyncStorage from "@react-native-async-storage/async-storage";
import MaterialServices from "../../services/MaterialServices";

const DetalleMaterialScreen = (props) => {
  const { route } = props;
  const { materialId } = route.params;
  const { isLoggedIn } = props.isLoggedIn || false;
  const [detalleMaterial, setDetalleMaterial] = useState(null);
  const [ejemplares, setEjemplares] = useState([]);

  const [menuVisible, setMenuVisible] = useState(false);

  const [showEjemplaresList, setShowEjemplareslist] = useState(false);
  const navigation = useNavigation();

  const [dialogVisible, setDialogVisible] = useState(false);

  const { userInfo } = useUser();

  useEffect(() => {
    const fetchDetalleMaterial = async () => {
      try {
        const response = await axios.get(
          `${API_BASE_URL}${API_ROUTES.MATERIALES}${materialId}/ejemplares/`
        );
        setDetalleMaterial(response.data.material);
        setEjemplares(response.data.ejemplares);
        console.log(response);
      } catch (error) {
        console.error("Error al obtener detales del material", error);
      }
    };
    fetchDetalleMaterial();
  }, [materialId]);

  const handleReservarMaterial = async () => {
    try {
      const access_token = userInfo.access_token;
      const owner_id = userInfo.id;

      const response = await MaterialServices.reservar(
        access_token,
        materialId,
        { id: owner_id }
      );
      console.log(response);
      setDialogVisible(false);
      toast.info(response.message);
      navigation.navigate("DetalleReserva", {
        reservaId: response.id,
      });
    } catch (error) {
      console.error("Error al realizar la reserva del material", error);
    }
  };
  const renderTableHeader = () => (
    <View style={tableStyles.tableHeader}>
      <Text style={tableStyles.headerText}>Id</Text>

      <Text style={tableStyles.headerText}>Estado</Text>
    </View>
  );

  const renderItem = ({ item }) => (
    <View style={tableStyles.tableRow}>
      <Text style={tableStyles.cell}>{item.id}</Text>

      <Text style={tableStyles.cell}>{item.estado}</Text>
    </View>
  );
  const handleConfirmar = async () => {
    if (!userInfo) {
      // Mostrar un mensaje y redireccionar al login
      toast.warning("Debes iniciar sesi√≥n para realizar una reserva.");
      navigation.navigate("Login"); // Aseg√∫rate de que el nombre de la pantalla sea correcto
      return;
    }
    if (userInfo.user.role === 1) {
      setShowEjemplareslist(true);
    } else {
      setDialogVisible(true);
    }

    setDialogVisible(true);
  };

  const handleCancel = () => {
    // Cerrar el di√°logo de confirmaci√≥n
    setDialogVisible(false);
  };

  const handleMenuPress = () => {
    setMenuVisible(true);
  };

  const handleActionSelect = (action) => {
    setMenuVisible(false); // Ocultar el men√∫ despu√©s de seleccionar una acci√≥n

    // L√≥gica para manejar diferentes acciones (por ejemplo, "Prestar material" o "Editar material")
    switch (action) {
      case "prestar":
        // L√≥gica para "Prestar material"
        // Puedes implementar esta l√≥gica seg√∫n tus necesidades
        break;
      case "editar":
        // L√≥gica para "Editar material"
        // Puedes implementar esta l√≥gica seg√∫n tus necesidades
        break;
      // Agrega m√°s casos seg√∫n sea necesario para otras acciones
      default:
        break;
    }
  };
  return (
    <PaperProvider>
      <View style={styles.container}>
        {userInfo && userInfo.user.role === 1 && (
          <Appbar.Header>
          <Appbar.BackAction onPress={() => navigation.goBack()} />
          <Appbar.Content title="Detalle Material" />
          <Appbar.Action icon="dots-vertical" onPress={() => setDialogVisible(true)} />
        </Appbar.Header>

        )}
        {detalleMaterial ? (
          <View style={styles.reservaContainer}>
            <Portal>
              {/* Di√°logo para mostrar las opciones de acciones */}
              <Dialog visible={menuVisible} onDismiss={() => setMenuVisible(false)}>
                <Dialog.Content>
                  <Paragraph>Selecciona una acci√≥n:</Paragraph>
                  <Button onPress={() => handleActionSelect("prestar")}>Prestar material</Button>
                  <Button onPress={() => handleActionSelect("editar")}>Editar material</Button>
                  {/* Agregar m√°s botones seg√∫n sea necesario */}
                </Dialog.Content>
              </Dialog>
            </Portal>
            <Text>Titulo: {detalleMaterial.titulo} </Text>
            <Text>
              Editorial:
              {detalleMaterial.editorial.map((editorial) => (
                <Text key={editorial.id}>{editorial.nombre}</Text>
              ))}
            </Text>
            <Text>
              Autor:
              {detalleMaterial.autor.map((autor) => (
                <Text key={autor.id}>
                  {autor.nombre}, {autor.apellido}.
                </Text>
              ))}
            </Text>
            <Text>
              carrera:
              {detalleMaterial.carrera.map((carrera) => (
                <Text key={carrera.id}>{carrera.nombre}</Text>
              ))}
            </Text>
            <Text>
              genero:
              {detalleMaterial.genero.map((genero) => (
                <Text key={genero.id}>{genero.nombre}</Text>
              ))}
            </Text>
            {showEjemplaresList && userInfo && userInfo.user.role === 1 ? (
              <ScrollView>
                {renderTableHeader()}
                <FlatList
                  data={ejemplares}
                  keyExtractor={(item) => item.id.toString()}
                  renderItem={renderItem}
                />
              </ScrollView>
            ) : detalleMaterial.estado === "Disponible" ? (
              <View style={styles.entregarContainer}>
                <Button style={styles.button} onPress={handleConfirmar}>
                  <Text style={[styles.buttonText, { color: "#FFFFFF" }]}>
                    {userInfo && userInfo.user.role === 1 ? (
                      <Text>Crear un prestamo</Text>
                    ) : (
                      <Text>Reservar</Text>
                    )}
                  </Text>
                </Button>
                <Portal>
                  <Dialog visible={dialogVisible} onDismiss={handleConfirmar}>
                    <Dialog.Content>
                      <Paragraph>
                        ¬øEstas seguro de realizar la reserva?. Recuerda que la
                        reserva tiene un lapso de tiempo de 24hs a partir de
                        este momento para retirar el ejemplar, finalizado el
                        tiempo la reserva ya no ser√° valida
                      </Paragraph>
                    </Dialog.Content>
                    <Dialog.Actions>
                      <Button onPress={handleCancel}>Cancelar</Button>
                      <Button onPress={handleReservarMaterial}>Aceptar</Button>
                    </Dialog.Actions>
                  </Dialog>
                </Portal>
              </View>
            ) : (
              detalleMaterial.estado === "Disponible (Lista de espera)" && (
                <View style={styles.entregarContainer}>
                  <Button style={styles.button} onPress={handleConfirmar}>
                    <Text style={[styles.buttonText, { color: "#FFFFFF" }]}>
                      Apuntarse a la lista de espera
                    </Text>
                  </Button>
                  <Portal>
                    <Dialog visible={dialogVisible} onDismiss={handleConfirmar}>
                      <Dialog.Content>
                        <Paragraph>
                          ¬øEstas seguro de a√±adirte a la "Lista de espera"?. Se
                          te notificar√° cuando el material est√© disponible y se
                          crear√° la reserva.
                        </Paragraph>
                      </Dialog.Content>
                      <Dialog.Actions>
                        <Button onPress={handleCancel}>Cancelar</Button>
                        <Button onPress={handleReservarMaterial}>
                          Aceptar
                        </Button>
                      </Dialog.Actions>
                    </Dialog>
                  </Portal>
                </View>
              )
            )}
          </View>
        ) : (
          <Text> Cargando detalles...</Text>
        )}
      </View>
    </PaperProvider>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    /* justifyContent: "center", */
    alignItems: "center",
    paddingHorizontal: 16,
  },
  reservaContainer: {
    width: "100%",
  },
  finalizadaText: {
    fontSize: 18,
    fontWeight: "bold",
    color: "green",
    marginBottom: 10,
  },
  entregarContainer: {
    marginTop: 10,
  },
  label: {
    fontSize: 16,
    marginBottom: 5,
  },
  input: {
    height: 40,
    borderColor: "gray",
    borderWidth: 1,
    marginBottom: 10,
    paddingHorizontal: 10,
  },
  flatList: {
    marginBottom: 10,
  },
  ejemplarText: {
    fontSize: 16,
  },
  button: {
    backgroundColor: "#2471A3",
    marginTop: 10,
  },
  loadingText: {
    fontSize: 18,
    fontStyle: "italic",
  },
});
export default DetalleMaterialScreen;
