import { View, Button } from "react-native";
import { TextInput } from "react-native-paper";
import React, { useState } from "react";
import { toast } from "react-toastify";

import AuthServices from "../../services/AuthServices";

const SignUpScreen = ({ setIsLoggedIn, navigation }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [nombre, setNombre] = useState("");
  const [apellido, setApellido] = useState("");
  const handleSignup = async () => {
    try {
      const response = await AuthServices.signup(
        email,
        password,
        nombre,
        apellido
      );
      if (response.email) {
        toast.success("Usuario registrado correctamente");
        setIsLoggedIn(false);
        navigation.navigate("AuthStack");
      }
    } catch (error) {
      console.log("error al registrarse: ", error);
    }
  };

  return (
    <View style={{ flex: 1, alignItems: "center", justifyContent: "center" }}>
      <TextInput placeholder="Email" onChangeText={setEmail} />
      <TextInput placeholder="Nombre" onChangeText={setNombre} />
      <TextInput placeholder="Email" onChangeText={setNombre} />
      <TextInput
        placeholder="Password"
        secureTextEntry={true}
        onChangeText={setPassword}
      />
      <Button styletitle="Registrase" onPress={handleSignup} />
    </View>
  );
};

export default SignUpScreen;
