import { View, Text,Button } from "react-native";
import React from "react";
import { useUser } from "../../contexts/UserContext";

const ProfileScreen = () => {
  const { userInfo } = useUser();
  console.log("user: ", userInfo);
  return (
    <View>      
      {userInfo && (
        <>          
          <Text>Email: {userInfo.user.email}</Text>
          <Text>Rol: {userInfo.user.role}</Text>
          <View style>

          </View>
          <Button title="Mis reservas"/><Button title="Mis prestamos"/>
        </>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  buttonContainer: {
    marginTop: 10, // Ajusta el espacio entre el texto y los botones seg√∫n tus necesidades
  },
});

export default ProfileScreen;
