import React, { useEffect } from "react";
import { View, Text } from "react-native";
import AsyncStorage from "@react-native-async-storage/async-storage";

const LogoutScreen = ({ navigate, setIsLoggedIn }) => {
  const handleLogout = async () => {
    try {
      await AsyncStorage.removeItem("access_token");
      if (setIsLoggedIn && typeof setIsLoggedIn === 'function'){
        setIsLoggedIn(false);
      }
      navigate("Login");
    } catch (error) {
      console.error("Error en el cierre de sesión:", error);
    }
  };

  // Llamar a handleLogout automáticamente al cargar la pantalla (opcional)
  useEffect(() => {
    handleLogout();
  }, []);

  return (
    <View>
      <Text>Cerrando Sesión...</Text>
      {/* Puedes agregar un spinner de carga aquí si lo deseas */}
    </View>
  );
};

export default LogoutScreen;
