import {
  View,
  Text,
  TouchableOpacity,
  FlatList,  
} from "react-native";
import React, { useEffect, useState } from "react";


import { API_BASE_URL, API_ROUTES } from "../../constants/API";
import axios from "axios";

const PrestamosScreen = ({ navigation }) => {
  const [prestamo, setPrestamo] = useState([]);

  useEffect(() => {
    const fetchPrestamo = async () => {
      try {
        const response = await axios.get(
          `${API_BASE_URL}${API_ROUTES.PRESTAMOS}`
        );
        console.log(response);
        setPrestamo(response.data);
      } catch (error) {
        console.log("Error al obtener los prestamos:", error);
      }
    };
    fetchPrestamo();
  }, []);
  const handlePrestamoPress = (prestamoId) => {
    navigation.navigate("DetallePrestamo", { prestamoId });
  };

  const renderTableHeader = () => (
    <View style={styles.tableHeader}>
      <Text style={styles.headerText}>Ejemplar</Text>
      <Text style={styles.headerText}>Usuario</Text>
      <Text style={styles.headerText}>Fecha finalizacion</Text>
    </View>
  );

  const renderItem = ({ item }) => (
    <TouchableOpacity onPress={() => handlePrestamoPress(item.id)}>
      <View style={styles.tableRow}>
        <Text style={styles.cell}>{item.ejemplar}</Text>
        <Text style={styles.cell}>{item.owner}</Text>
        <Text style={styles.cell}>{item.fecha_fin}</Text>
      </View>
    </TouchableOpacity>
  );

  return (
    <View>      
      {renderTableHeader()}
      <FlatList
        data={prestamo}
        keyExtractor={(item) => item.id.toString()}
        renderItem={renderItem}
      />
    </View>
  );
};


export default PrestamosScreen;
