import { View, Text, ScrollView, StyleSheet } from "react-native";
import React, { useState } from "react";
import { TextInput, Button, Snackbar, HelperText } from "react-native-paper";
const NuevoMaterialScreen = ({ navigation }) => {
  const [titulo, setTitulo] = useState("");
  const [editorial, setEditorial] = useState("");
  const [autor, setAutor] = useState("");
  const [carrera, setCarrera] = useState("");
  const [genero, setGenero] = useState("");

  const [snackbarVisible, setSnackbarVisible] = useState(false);

  const handleGuardarMaterial = () => {
    // Aquí puedes realizar la lógica para guardar el nuevo material
    // Puedes utilizar MaterialServices.crearMaterial() o axios.post() para enviar los datos al servidor

    // Muestra un mensaje de éxito con Snackbar
    setSnackbarVisible(true);
  };

  return (
    <ScrollView contentContainerStyle={styles.container}>
      <TextInput
        label="Título"
        value={titulo}
        onChangeText={(text) => setTitulo(text)}
      />
      <TextInput
        label="Editorial"
        value={editorial}
        onChangeText={(text) => setEditorial(text)}
      />
      <TextInput
        label="Autor"
        value={autor}
        onChangeText={(text) => setAutor(text)}
      />
      <TextInput
        label="Carrera"
        value={carrera}
        onChangeText={(text) => setCarrera(text)}
      />
      <TextInput
        label="Género"
        value={genero}
        onChangeText={(text) => setGenero(text)}
      />

      <Button style={styles.button} onPress={handleGuardarMaterial}>
        Guardar Material
      </Button>

      <Snackbar
        visible={snackbarVisible}
        onDismiss={() => setSnackbarVisible(false)}
      >
        Material guardado con éxito.
      </Snackbar>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    padding: 16,
  },
  button: {
    marginTop: 16,
  },
});

export default NuevoMaterialScreen;
