import React, { useState, useEffect } from "react";
import { NavigationContainer } from "@react-navigation/native";
import { createStackNavigator } from "@react-navigation/stack";
import AsyncStorage from "@react-native-async-storage/async-storage";

// IMPORTAR PANTALLAS
import LoginScreen from "./screens/auth/LoginScreen";
import HomeScreen from "./screens/HomeScreen";
import MaterialListScreen from "./screens/materiales/MaterialListScreen";
import DetalleMaterialScreen from "./screens/materiales/DetalleMaterialScreen";

const Stack = createStackNavigator();

const App = (navigat) => {
  const [isLoggedIn, setIsLoggedIn] = useState(null);

  useEffect(() => {
    const checkAuthentication = async () => {
      try {
        const token = await AsyncStorage.getItem("access_token");
        setIsLoggedIn(!!token);
        console.log("isLoggedIn", isLoggedIn);
      } catch (error) {
        console.error("error al autenticar", error);
        setIsLoggedIn(false);
      }
    };
    checkAuthentication();
  }, []);

  if (isLoggedIn === null) {
    return null;
  }
  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName={isLoggedIn ? "Home" : "Login"}>
        {isLoggedIn ? (
          <>
            <Stack.Screen
              name="Home"
              component={HomeScreen}
              options={{ headerShown: false }}
            />
            <Stack.Screen name="MaterialList" component={MaterialListScreen} />
            <Stack.Screen
              name="DetalleMaterialScreen"
              component={DetalleMaterialScreen}
            />
          </>
        ) : (
          <>
            <Stack.Screen
              name="Login"
              children={(props) => (
                <LoginScreen {...props} setIsLoggedIn={setIsLoggedIn} />
              )}
            />
          </>
        )}
      </Stack.Navigator>
    </NavigationContainer>
  );
};

export default App;
