import { View, Text, StyleSheet, ScrollView } from "react-native";
import React, { useState, useEffect } from "react";
import { TextInput, List } from "react-native-paper";
import axios from "axios";
import MaterialServices from "../../../services/MaterialServices";
import AuthServices from "../../../services/AuthServices";
import { useUser } from "../../../contexts/UserContext";
const PrestarMaterialScreen = ({ route, navigation }) => {
  const { detalleMaterial, ejemplares } = route.params;
  const [users, setUsers] = useState([]);
  const [ejemplar, setEjemplar] = useState([]);
  const { userInfo } = useUser();
  const accessToken = userInfo.access_token;

  const [filteredUsers, setFilteredUsers] = useState([]);
  const [filteredEjemplares, setFilteredEjemplares] = useState([]);

  const [selectedUser, setSelectedUser] = useState(null);
  const [selectedEjemplar, setSelectedEjemplar] = useState("");

  const [snackbarVisible, setSnackbarVisible] = useState(false);

  console.log(detalleMaterial);
  console.log(ejemplares);

  useEffect(() => {
    navigation.setOptions({
      title: detalleMaterial.titulo,
    });

  }, [detalleMaterial, ejemplares, accessToken]);

  const handlePrestarMaterial = () => {
    // Aquí puedes realizar la lógica para guardar el nuevo material
    // Puedes utilizar MaterialServices.crearMaterial() o axios.post() para enviar los datos al servidor

    // Muestra un mensaje de éxito con Snackbar
    setSnackbarVisible(true);
  };

  const filterItems = (text, items) => {
    return text
      ? items.filter((item) => item.id.toString().includes(text))
      : [];
  };

  const selectUser = (selectedUser) => {
    setSelectedUser(selectedUser);
    setFilteredUsers([]);
  };

  const filterUsers = async (text) => {
    try {
      const usersResponse = await AuthServices.listarUsuariosNoAdmin(
        accessToken,
        text
      );
      setFilteredUsers(usersResponse);
    } catch (error) {
      console.error("Error al filtrar usuarios", error);
    }
  };

  const selectEjemplar = (selectedEjemplar) => {
    setEjemplar(selectedEjemplar.id);
    setFilteredEjemplares([]);
  };
  const filterEjemplares = (text) => {
    const filtered = filterItems(text, ejemplares);
    setFilteredEjemplares(filtered);
  };
  return (
    <ScrollView>
      <TextInput
        label="Ingrese el Id del ejemplar a prestar"
        value={ejemplar}
        onChangeText={(text) => {
          setEjemplar(text);
          filterEjemplares(text);
        }}
      />
      <List.Section>
        {filteredEjemplares.map((item) => (
          <List.Item
            key={item.id}
            title={`ID: ${item.id} - ${item.estado}`}
            onPress={() => selectEjemplar(item)}
          />
        ))}
      </List.Section>

      <TextInput
        label="Buscar Usuario por Email"
        value={selectedUser ? selectedUser.email : ""}
        onChangeText={(text) => {
          setSelectedUser(null); // Limpiar el usuario seleccionado al cambiar el texto
          filterUsers(text);
        }}
      />
      {/* Lista de usuarios filtrados */}
      <List.Section>
        {filteredUsers.map((user) => (
          <List.Item
            key={user.id}
            title={`Email: ${user.email} - Role: ${user.role}`}
            onPress={() => selectUser(user)}
          />
        ))}
      </List.Section>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    padding: 16,
  },
  button: {
    marginTop: 16,
  },
  label: {
    fontSize: 16,
    marginBottom: 5,
  },
  input: {
    height: 40,
    borderColor: "gray",
    borderWidth: 1,
    marginBottom: 10,
    paddingHorizontal: 10,
  },
});

export default PrestarMaterialScreen;
