import { View, Text, StyleSheet } from "react-native";
import React, { useState, useEffect } from "react";
import {
  TextInput,
  Button,
  Snackbar,
  HelperText,
  List,
} from "react-native-paper";

import MaterialServices from "../../../services/MaterialServices";

const PrestarMaterialScreen = ({ route, navigation }) => {
  const { detalleMaterial } = route.params;
  const [ejemplares, setEjemplares] = useState({});
  console.log(detalleMaterial);

  useEffect(() => {
    navigation.setOptions({
      title: detalleMaterial.titulo,
    });
    const fetchEjemplaresDisponibles = async () => {
      try {
        const response = await axios.get(
          `${API_BASE_URL}${API_ROUTES.MATERIALES}${materialId}/ejemplares/`
        );
        setEjemplares(response.data.ejemplares);
        console.log(ejemplares);
      } catch (error) {
        console.error("Error al obtener los ejemplares disponibles");
      }
    };
    fetch()
  }, [detalleMaterial]);
  return (
    <View>
      <Text>PrestarMaterialScreen</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    padding: 16,
  },
  button: {
    marginTop: 16,
  },
  label: {
    fontSize: 16,
    marginBottom: 5,
  },
  input: {
    height: 40,
    borderColor: "gray",
    borderWidth: 1,
    marginBottom: 10,
    paddingHorizontal: 10,
  },
});

export default PrestarMaterialScreen;
