import React, { useEffect } from "react";
import { View, Text, Button } from "react-native";
import AsyncStorage from "@react-native-async-storage/async-storage";

const LogoutScreen = ({ navigation, setIsLoggedIn }) => {
  const handleLogout = async () => {
    try {
      await AsyncStorage.removeItem("access_token");
      if (setIsLoggedIn && typeof setIsLoggedIn === "function") {
        setIsLoggedIn(false);
        navigation.navigate("Login");
      }
    } catch (error) {
      console.error("Error en el cierre de sesión:", error);
    }
  };

  // Llamar a handleLogout automáticamente al cargar la pantalla (opcional)
  useEffect(() => {
    handleLogout();
  }, []);

  return (
    <View>
      <Text>¿Desea cerrar la sesion?</Text>
      <Button titlle="Confirmar" onPres={hand}/>

      {/* Puedes agregar un spinner de carga aquí si lo deseas */}
    </View>
  );
};

export default LogoutScreen;
