import React from "react";
import { View, Text, Button, StyleSheet } from "react-native";
import { useNavigation } from "@react-navigation/native";
import MaterialListScreen from "./materiales/MaterialListScreen";
import { createStackNavigator } from "@react-navigation/stack";
import DetalleMaterialScreen from "./materiales/DetalleMaterialScreen";

const Stack = createStackNavigator();

function MaterialStack() {
  return (
    <Stack.Navigator>
      <Stack.Screen name="MaterialList" component={MaterialListScreen} />
      <Stack.Screen name="DetalleMaterial" component={DetalleMaterialScreen} />
    </Stack.Navigator>
  );
}
const HomeScreen = () => {
  const navigation = useNavigation();
  return (
    <View style={styles.container}>
      {/* Barra de navegación */}
      <View style={styles.navBar}>
        <Text style={styles.title}>SysBib</Text>

      </View>

      {/* Contenido principal */}
      <MaterialListScreen navigation={navigation} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  navBar: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    padding: 10,
    backgroundColor: "#3498db", // Puedes cambiar el color según tus preferencias
  },
  title: {
    fontSize: 20,
    fontWeight: "bold",
    color: "#fff", // Color del texto
  },
});

export default HomeScreen;
